function composeHtml( $postID ) : array {

    $link = '';
    $postType = get_post_type( $postID );

    if (  $postType == 'mogul_videos'  ) {
        $link = 'https://mogulpictures.co.za/showcase-videos/?video_id=' . $postID;
    } else if (  $postType == 'mogul_works'  ) {
        $link = 'https://mogulpictures.co.za/showcase-work/?work_id=' . $postID;
    }

    $title = get_field ( 'title', $postID );
    $client = get_field ( 'client', $postID );
    $previewVidLink = get_field( 'preview_video_link', $postID );
    $thumb = 'https://mogulpictures.co.za/wp-content/uploads/2014/05/parallax01.jpg'; // default thumbnail

    if( has_post_thumbnail( $postID )) {
        $thumb = wp_get_attachment_image_src( get_post_thumbnail_id( $postID ), 'full');
    }

    $arrVals = array(
        'link' => $link,
        'title' => $title,
        'client' => $client,
        'thumb' => $thumb,
        'previewVidLink' => $previewVidLink
    );

    

    return $arrVals;
}


